import React, { Component } from 'react';
import { Form, Button } from 'react-bootstrap';
import '../stylesheets/Welcome.css';
import { connect } from 'react-redux'; 
import axios from 'axios';


class Welcome extends Component {

    log_in = (userdata) => {
        this.props.dispatch({
            type:"LOG_IN"
        });
        this.props.dispatch({
            type:"USER_DATA",
            payload: userdata
        })
    }

    loginRequest = () => {
        var encodedURI = window.encodeURI(this.props.uri);
        console.log(encodedURI);
        return axios.get(encodedURI).then(response => {
            this.log_in(response);
        })
    }

    componentDidMount() {
        this.loginRequest();
    }

    render() {
        return (
            <div className='Welcome'>
                
                <div className='Welcome-Text-Container'>
                    <div className='Welcome-Text'>
                        Welcome to <span className='App-Welcome-Title'>Blogsite</span> !
                        <p>The <i><b>perfect</b></i> place to tell any story â€”Please login or sign up!</p>
                    </div>
                </div>

                <div className='Welcome-Line'></div>

                <div className='Welcome-Form-Container'>
                    <div className='Welcome-Form'>
                        <Form>
                            <Form.Group controlId="Form-Email">
                                <Form.Label className='Login-Label'>Sign in:</Form.Label>
                                <Form.Control type='email' size='lg' placeholder='Email' name='email'></Form.Control>
                            </Form.Group>

                            <Form.Group controlId="Form-Password">
                                <Form.Control type='password' size='lg' placeholder='Password' name='password'></Form.Control>
                            </Form.Group>
                            <Button type='button' 
                                    variant='dark'
                                    size='lg'
                                    onClick={this.log_in}>Login</Button>
                        </Form>
                    </div>
                </div>

            </div>
        )
     }
}

function mapStateToProps(state) {
    return {
        isLogged: state.isLogged
    }
}

export default connect(mapStateToProps)(Welcome);
