import React, { Component } from 'react'
import '../stylesheets/BlogpostForm.css';
import { Form, Button } from 'react-bootstrap';



let blogName = '';

class CreateBlogPost extends Component {
    render() {
        return (
            <div className='Blogpost-Form'>
                <Form className="Form">
                    <Form.Group className="Blog-Title">
                        <Form.Control 
                            type='text'
                            size='lg'
                            placeholder='Title...'
                            name='blogName'
                            onChange={event => blogName = event.target.value} />
                    </Form.Group>
                    <Form.Group className="Checkboxes">
                        <Form.Check inline label="Academic" name="academic" type='checkbox' />
                    </Form.Group>
                </Form>
            </div>
        )
    }
}


export default CreateBlogPost;
